animations.css.content:
/* AttendIQ - Animations CSS */

/* Fade In Animation */
@keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
}

.fade-in {
    animation: fadeIn 0.5s ease forwards;
}

/* Staggered fade-in for lists and tables */
.fade-in-item {
    opacity: 0;
    transform: translateY(10px);
}

.fade-in-item.visible {
    animation: fadeIn 0.5s ease forwards;
}

/* Apply staggered delay to children */
.stagger-fade > *:nth-child(1) { animation-delay: 0.1s; }
.stagger-fade > *:nth-child(2) { animation-delay: 0.2s; }
.stagger-fade > *:nth-child(3) { animation-delay: 0.3s; }
.stagger-fade > *:nth-child(4) { animation-delay: 0.4s; }
.stagger-fade > *:nth-child(5) { animation-delay: 0.5s; }
.stagger-fade > *:nth-child(6) { animation-delay: 0.6s; }
.stagger-fade > *:nth-child(7) { animation-delay: 0.7s; }
.stagger-fade > *:nth-child(8) { animation-delay: 0.8s; }
.stagger-fade > *:nth-child(9) { animation-delay: 0.9s; }
.stagger-fade > *:nth-child(10) { animation-delay: 1s; }

/* Pulse Animation */
@keyframes pulse {
    0% { transform: scale(1); }
    50% { transform: scale(1.05); }
    100% { transform: scale(1); }
}

.pulse {
    animation: pulse 2s infinite;
}

/* Rotate Animation */
@keyframes rotate {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
}

.rotate {
    animation: rotate 2s linear infinite;
}

/* Shimmer Effect */
@keyframes shimmer {
    0% { background-position: -1000px 0; }
    100% { background-position: 1000px 0; }
}

.shimmer {
    background: linear-gradient(90deg, rgba(255,255,255,0), rgba(255,255,255,0.2), rgba(255,255,255,0));
    background-size: 1000px 100%;
    animation: shimmer 2s infinite;
}

/* QR Code Animation */
@keyframes qrPulse {
    0% { box-shadow: 0 0 0 0 rgba(59, 130, 246, 0.4); }
    70% { box-shadow: 0 0 0 10px rgba(59, 130, 246, 0); }
    100% { box-shadow: 0 0 0 0 rgba(59, 130, 246, 0); }
}

.qr-active {
    animation: qrPulse 2s infinite;
}

/* Countdown Animation */
@keyframes countdown {
    0% { stroke-dashoffset: 0; }
    100% { stroke-dashoffset: 283; } /* 2*PI*r where r=45 */
}

.countdown-circle {
    stroke-dasharray: 283;
    stroke-dashoffset: 0;
    transform-origin: center;
    transform: rotate(-90deg);
}

.countdown-active {
    animation: countdown 30s linear forwards;
}

/* Alert Animation */
@keyframes alertBlink {
    0% { opacity: 1; }
    50% { opacity: 0.5; }
    100% { opacity: 1; }
}

.alert-blink {
    animation: alertBlink 1s ease infinite;
}

/* Scan Entry Animation */
@keyframes scanEntrySlide {
    from { transform: translateX(-20px); opacity: 0; }
    to { transform: translateX(0); opacity: 1; }
}

.scan-entry-new {
    animation: scanEntrySlide 0.3s ease forwards;
}

/* Chart Animation */
@keyframes chartGrow {
    from { height: 0; opacity: 0; }
    to { height: 100%; opacity: 1; }
}

.chart-bar {
    animation: chartGrow 1s ease forwards;
}

/* Status Badge Animation */
@keyframes statusPop {
    0% { transform: scale(0.8); }
    50% { transform: scale(1.1); }
    100% { transform: scale(1); }
}

.status-badge-new {
    animation: statusPop 0.3s ease forwards;
}

/* Page Transition */
.page-transition {
    transition: opacity 0.3s, transform 0.3s;
}

.page-transition.loading {
    opacity: 0;
    transform: translateY(10px);
}

/* Loading Spinner */
@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.loading-spinner {
    width: 24px;
    height: 24px;
    border: 3px solid rgba(255, 255, 255, 0.1);
    border-top-color: var(--color-primary);
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

/* Button Hover Effect */
.btn-hover-effect {
    position: relative;
    overflow: hidden;
}

.btn-hover-effect::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background-color: rgba(255, 255, 255, 0.2);
    border-radius: 50%;
    transform: translate(-50%, -50%);
    transition: width 0.6s, height 0.6s;
}

.btn-hover-effect:hover::after {
    width: 300%;
    height: 300%;
}

/* Number Counter Animation */
.number-animate {
    transition: transform 0.5s, opacity 0.5s;
}

.number-animate.updating {
    transform: translateY(-10px);
    opacity: 0;
}

/* Floating Animation for Decorative Elements */
@keyframes float {
    0% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
    100% { transform: translateY(0px); }
}

.float {
    animation: float 5s ease-in-out infinite;
}

/* Particle Animation */
.particles {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
    z-index: -1;
}

.particle {
    position: absolute;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.1);
    pointer-events: none;
}

/* Responsive Animation Adjustments */
@media (max-width: 768px) {
    .stagger-fade > * {
        animation-delay: 0.1s !important;
    }

    .float {
        animation: none;
    }
}

style.css.content:
/* AttendIQ - Faculty Dashboard Styles */

/* CSS Reset */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

/* Variables */
:root {
    --color-primary: #3b82f6;
    --color-primary-dark: #2563eb;
    --color-secondary: #8b5cf6;
    --color-success: #22c55e;
    --color-warning: #eab308;
    --color-danger: #ef4444;
    --color-info: #0ea5e9;
    --color-dark: #0f172a;
    --color-light: #f8fafc;
    --color-gray: #64748b;
    --color-gray-light: #94a3b8;
    --color-gray-dark: #334155;
    --gradient-primary: linear-gradient(135deg, #3b82f6, #8b5cf6);
    --shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.1);
    --shadow-md: 0 4px 6px rgba(0, 0, 0, 0.1);
    --shadow-lg: 0 10px 15px rgba(0, 0, 0, 0.1);
    --radius-sm: 0.25rem;
    --radius-md: 0.5rem;
    --radius-lg: 1rem;
}

/* Base Styles */
body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
    background-color: var(--color-dark);
    color: var(--color-light);
    line-height: 1.5;
}

a {
    color: var(--color-primary);
    text-decoration: none;
    transition: color 0.2s;
}

a:hover {
    color: var(--color-primary-dark);
}

/* Layout */
.container {
    width: 100%;
    max-width: 1280px;
    margin: 0 auto;
    padding: 0 1rem;
}

.dashboard {
    display: grid;
    grid-template-columns: 240px 1fr;
    min-height: 100vh;
}

/* Navigation */
.nav {
    background-color: rgba(15, 23, 42, 0.8);
    border-right: 1px solid rgba(255, 255, 255, 0.1);
    padding: 1.5rem;
    position: fixed;
    height: 100vh;
    width: 240px;
    overflow-y: auto;
}

.nav-logo {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    margin-bottom: 2rem;
}

.nav-logo img {
    width: 32px;
    height: 32px;
}

.nav-logo h1 {
    font-size: 1.25rem;
    font-weight: 700;
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
}

.nav-menu {
    list-style: none;
}

.nav-menu-title {
    font-size: 0.75rem;
    text-transform: uppercase;
    letter-spacing: 0.05em;
    color: var(--color-gray-light);
    margin: 1.5rem 0 0.5rem;
}

.nav-menu-item {
    margin-bottom: 0.25rem;
}

.nav-menu-link {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.75rem;
    border-radius: var(--radius-md);
    color: var(--color-gray-light);
    transition: all 0.2s;
}

.nav-menu-link:hover {
    background-color: rgba(255, 255, 255, 0.05);
    color: var(--color-light);
}

.nav-menu-link.active {
    background-color: rgba(59, 130, 246, 0.1);
    color: var(--color-primary);
}

.nav-menu-link i {
    width: 1rem;
    text-align: center;
}

/* Main Content */
.main {
    padding: 1.5rem;
    margin-left: 240px;
}

/* Header */
.header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 2rem;
}

.header-title h1 {
    font-size: 1.5rem;
    font-weight: 700;
}

.header-title p {
    color: var(--color-gray-light);
    margin-top: 0.25rem;
}

.header-actions {
    display: flex;
    gap: 0.5rem;
}

/* Stats */
.stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
    margin-bottom: 1.5rem;
}

.stat-card {
    background-color: rgba(255, 255, 255, 0.05);
    border-radius: var(--radius-md);
    padding: 1.5rem;
    transition: transform 0.2s;
}

.stat-card:hover {
    transform: translateY(-2px);
}

.stat-card-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
}

.stat-card-title {
    font-size: 0.875rem;
    color: var(--color-gray-light);
}

.stat-card-icon {
    width: 2rem;
    height: 2rem;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: var(--radius-sm);
    background-color: rgba(59, 130, 246, 0.1);
    color: var(--color-primary);
}

.stat-card-value {
    font-size: 1.5rem;
    font-weight: 700;
}

.stat-card-desc {
    font-size: 0.875rem;
    color: var(--color-gray-light);
    margin-top: 0.25rem;
}

/* Cards */
.card {
    background-color: rgba(255, 255, 255, 0.05);
    border-radius: var(--radius-md);
    padding: 1.5rem;
    margin-bottom: 1.5rem;
}

.card-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
}

.card-title {
    font-size: 1.125rem;
    font-weight: 600;
}

.card-actions {
    display: flex;
    gap: 0.5rem;
}

/* Grid */
.grid {
    display: grid;
    gap: 1.5rem;
}

.grid-2 {
    grid-template-columns: repeat(2, 1fr);
}

.grid-3 {
    grid-template-columns: repeat(3, 1fr);
}

/* Forms */
.form-group {
    margin-bottom: 1rem;
}

.form-label {
    display: block;
    margin-bottom: 0.5rem;
    font-size: 0.875rem;
    color: var(--color-gray-light);
}

.form-control {
    width: 100%;
    padding: 0.75rem;
    background-color: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: var(--radius-md);
    color: var(--color-light);
    transition: border-color 0.2s;
}

.form-control:focus {
    outline: none;
    border-color: var(--color-primary);
}

.form-control::placeholder {
    color: var(--color-gray);
}

/* Buttons */
.btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    padding: 0.75rem 1rem;
    border-radius: var(--radius-md);
    font-weight: 500;
    transition: all 0.2s;
    cursor: pointer;
    border: none;
}

.btn-sm {
    padding: 0.5rem 0.75rem;
    font-size: 0.875rem;
}

.btn-lg {
    padding: 1rem 1.5rem;
    font-size: 1.125rem;
}

.btn-icon {
    padding: 0.5rem;
    border-radius: var(--radius-sm);
}

.btn-primary {
    background-color: var(--color-primary);
    color: white;
}

.btn-primary:hover {
    background-color: var(--color-primary-dark);
}

.btn-secondary {
    background-color: var(--color-secondary);
    color: white;
}

.btn-success {
    background-color: var(--color-success);
    color: white;
}

.btn-danger {
    background-color: var(--color-danger);
    color: white;
}

.btn-warning {
    background-color: var(--color-warning);
    color: white;
}

.btn-outline {
    background-color: transparent;
    border: 1px solid rgba(255, 255, 255, 0.1);
    color: var(--color-light);
}

.btn-outline:hover {
    background-color: rgba(255, 255, 255, 0.05);
}

/* QR Code */
.qr-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
}

.qr-code {
    padding: 1rem;
    background-color: white;
    border-radius: var(--radius-md);
}

.qr-info {
    text-align: center;
}

.qr-timer {
    font-size: 1.5rem;
    font-weight: 700;
    margin-bottom: 0.5rem;
}

.qr-controls {
    display: flex;
    gap: 0.5rem;
    margin-top: 1rem;
}

/* Lists */
.list {
    list-style: none;
}

.list li {
    display: flex;
    justify-content: space-between;
    padding: 0.75rem 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.list li:last-child {
    border-bottom: none;
}

.list-value {
    font-weight: 600;
}

/* Student List Styles */
.student-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 1rem;
}

.student-table th,
.student-table td {
    padding: 0.75rem;
    text-align: left;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.student-table th {
    font-weight: 600;
    color: var(--color-light);
    background-color: rgba(255, 255, 255, 0.05);
}

.student-table tr:hover {
    background-color: rgba(255, 255, 255, 0.05);
}

.status-badge {
    display: inline-block;
    padding: 0.25rem 0.5rem;
    border-radius: 1rem;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
}

.status-present {
    background-color: rgba(34, 197, 94, 0.2);
    color: #22c55e;
}

.status-absent {
    background-color: rgba(239, 68, 68, 0.2);
    color: #ef4444;
}

.status-late {
    background-color: rgba(249, 115, 22, 0.2);
    color: #f97316;
}

.attendance-rate {
    font-weight: 600;
}

.rate-excellent {
    color: #22c55e;
}

.rate-good {
    color: #eab308;
}

.rate-poor {
    color: #ef4444;
}

.table-controls {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
}

.table-filters {
    display: flex;
    gap: 1rem;
}

.pagination {
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.pagination button {
    background-color: rgba(255, 255, 255, 0.1);
    border: none;
    color: var(--color-light);
    padding: 0.5rem 0.75rem;
    border-radius: 0.25rem;
    cursor: pointer;
    transition: background-color 0.2s;
}

.pagination button:hover:not(:disabled) {
    background-color: rgba(255, 255, 255, 0.2);
}

.pagination button:disabled {
    opacity: 0.5;
    cursor: not-allowed;
}

/* Chart Styles */
.chart-container {
    height: 250px;
    margin-top: 1rem;
    position: relative;
}

/* Live Feed Styles */
.feed-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
    padding: 0.75rem;
    background-color: rgba(255, 255, 255, 0.05);
    border-radius: 0.5rem;
}

.feed-stats {
    display: flex;
    gap: 1.5rem;
}

.feed-stat {
    display: flex;
    flex-direction: column;
    align-items: center;
}

.feed-stat-value {
    font-size: 1.5rem;
    font-weight: 700;
}

.feed-stat-label {
    font-size: 0.75rem;
    opacity: 0.7;
}

.feed-controls {
    display: flex;
    gap: 0.5rem;
    align-items: center;
}

.scan-feed {
    max-height: 400px;
    overflow-y: auto;
    padding-right: 0.5rem;
}

.scan-entry {
    display: flex;
    padding: 0.75rem;
    border-radius: 0.5rem;
    margin-bottom: 0.5rem;
    background-color: rgba(255, 255, 255, 0.05);
    transition: transform 0.2s;
}

.scan-entry:hover {
    transform: translateY(-2px);
}

.scan-entry.present {
    border-left: 4px solid #22c55e;
}

.scan-entry.absent {
    border-left: 4px solid #ef4444;
}

.scan-entry.late {
    border-left: 4px solid #f97316;
}

.scan-status {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 2.5rem;
    font-size: 1.25rem;
}

.scan-entry.present .scan-status i {
    color: #22c55e;
}

.scan-entry.absent .scan-status i {
    color: #ef4444;
}

.scan-entry.late .scan-status i {
    color: #f97316;
}

.scan-details {
    flex: 1;
}

.scan-details h4 {
    margin: 0;
    font-size: 0.9rem;
}

.scan-details p {
    margin: 0.25rem 0 0;
    font-size: 0.8rem;
    opacity: 0.7;
}

.override-badge {
    display: inline-block;
    font-size: 0.7rem;
    background-color: rgba(139, 92, 246, 0.2);
    color: #8b5cf6;
    padding: 0.1rem 0.3rem;
    border-radius: 0.25rem;
    margin-top: 0.25rem;
}

.scan-time {
    font-size: 0.8rem;
    opacity: 0.7;
    align-self: flex-start;
}

.alert-bar {
    background-color: rgba(239, 68, 68, 0.2);
    color: #ef4444;
    padding: 0.75rem;
    border-radius: 0.5rem;
    margin-bottom: 1rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;
    display: none;
}

.alert-bar i {
    font-size: 1.25rem;
}

/* Responsive adjustments */
@media (max-width: 768px) {
    .dashboard {
        grid-template-columns: 1fr;
    }

    .nav {
        position: fixed;
        left: -240px;
        z-index: 100;
        transition: left 0.3s;
    }

    .nav.open {
        left: 0;
    }

    .main {
        margin-left: 0;
    }

    .grid-2,
    .grid-3 {
        grid-template-columns: 1fr;
    }

    .table-controls {
        flex-direction: column;
        align-items: flex-start;
        gap: 1rem;
    }

    .table-filters {
        flex-direction: column;
        width: 100%;
    }

    .feed-header {
        flex-direction: column;
        gap: 1rem;
    }

    .feed-stats {
        width: 100%;
        justify-content: space-between;
    }
}

css/animations.css.content:
/* Animations */
@keyframes gradient-shift {
    0%, 100% {
        background-position: 0% 50%;
    }
    50% {
        background-position: 100% 50%;
    }
}

@keyframes float {
    0%, 100% {
        transform: translateY(0px);
    }
    50% {
        transform: translateY(-20px);
    }
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes slideInLeft {
    from {
        opacity: 0;
        transform: translateX(-50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes slideInRight {
    from {
        opacity: 0;
        transform: translateX(50px);
    }
    to {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes scaleIn {
    from {
        opacity: 0;
        transform: scale(0.8);
    }
    to {
        opacity: 1;
        transform: scale(1);
    }
}

@keyframes glow {
    0%, 100% {
        box-shadow: 0 0 20px rgba(59, 130, 246, 0.5);
    }
    50% {
        box-shadow: 0 0 30px rgba(59, 130, 246, 0.8);
    }
}

@keyframes pulse {
    0%, 100% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.05);
    }
}

@keyframes bounce {
    0%, 20%, 50%, 80%, 100% {
        transform: translateY(0);
    }
    40% {
        transform: translateY(-10px);
    }
    60% {
        transform: translateY(-5px);
    }
}

/* Animation Classes */
.fade-in {
    animation: fadeIn 0.8s ease-out;
}

.slide-in-left {
    animation: slideInLeft 0.8s ease-out;
}

.slide-in-right {
    animation: slideInRight 0.8s ease-out;
}

.scale-in {
    animation: scaleIn 0.6s ease-out;
}

.glow {
    animation: glow 2s ease-in-out infinite;
}

.pulse {
    animation: pulse 2s ease-in-out infinite;
}

.bounce {
    animation: bounce 2s ease-in-out infinite;
}

/* Hover Effects */
.feature-card:hover .feature-icon {
    animation: pulse 0.6s ease-in-out;
}

.stat-card:hover {
    animation: scaleIn 0.3s ease-out;
}

.btn:hover {
    animation: pulse 0.3s ease-in-out;
}

/* Scroll Animations */
.scroll-animate {
    opacity: 0;
    transform: translateY(50px);
    transition: all 0.8s ease;
}

.scroll-animate.animate {
    opacity: 1;
    transform: translateY(0);
}

/* Loading Animation */
.loading {
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 3px solid rgba(255, 255, 255, 0.3);
    border-radius: 50%;
    border-top-color: #fff;
    animation: spin 1s ease-in-out infinite;
}

@keyframes spin {
    to {
        transform: rotate(360deg);
    }
}

/* Particle Animation */
.particles::before {
    animation: float 6s ease-in-out infinite;
}

/* Gradient Text Animation */
.gradient-text {
    animation: gradient-shift 3s ease infinite;
}

/* Navbar Animation */
.navbar {
    animation: slideInDown 0.5s ease-out;
}

@keyframes slideInDown {
    from {
        transform: translateY(-100%);
        opacity: 0;
    }
    to {
        transform: translateY(0);
        opacity: 1;
    }
}

/* Hero Content Animation */
.hero-text {
    animation: fadeIn 1s ease-out 0.3s both;
}

.hero-stats {
    animation: fadeIn 1s ease-out 0.6s both;
}

/* Feature Cards Animation */
.feature-card {
    animation: fadeIn 0.8s ease-out;
}

.feature-card:nth-child(1) { animation-delay: 0.1s; }
.feature-card:nth-child(2) { animation-delay: 0.2s; }
.feature-card:nth-child(3) { animation-delay: 0.3s; }
.feature-card:nth-child(4) { animation-delay: 0.4s; }

/* Smooth Scrolling */
html {
    scroll-behavior: smooth;
}

/* Custom Scrollbar */
::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    background: rgba(255, 255, 255, 0.1);
}

::-webkit-scrollbar-thumb {
    background: linear-gradient(45deg, #3b82f6, #8b5cf6);
    border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
    background: linear-gradient(45deg, #2563eb, #7c3aed);
}

/* Mobile Menu Animation */
.nav-menu {
    transition: left 0.3s ease;
}

.nav-menu.active {
    animation: slideInLeft 0.3s ease-out;
}

/* Button Hover Effects */
.btn-primary:hover {
    animation: glow 1s ease-in-out infinite;
}

.btn-secondary:hover {
    animation: pulse 0.3s ease-in-out;
}

/* Card Hover Effects */
.feature-card:hover {
    animation: scaleIn 0.3s ease-out;
}

.stat-card:hover {
    animation: bounce 0.6s ease-in-out;
}

/* Image Hover Effects */
.image-placeholder:hover {
    animation: pulse 0.6s ease-in-out;
}

/* Footer Animation */
.footer {
    animation: fadeIn 1s ease-out 0.8s both;
}

/* Responsive Animations */
@media (max-width: 768px) {
    .hero-title {
        animation: fadeIn 1s ease-out 0.2s both;
    }

    .hero-description {
        animation: fadeIn 1s ease-out 0.4s both;
    }

    .hero-buttons {
        animation: fadeIn 1s ease-out 0.6s both;
    }
}

/* Performance Optimizations */
@media (prefers-reduced-motion: reduce) {
    * {
        animation-duration: 0.01ms !important;
        animation-iteration-count: 1 !important;
        transition-duration: 0.01ms !important;
    }
}

css/style.css.content:
/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --primary-color: #3b82f6;
    --secondary-color: #8b5cf6;
    --accent-color: #ec4899;
    --dark-bg: #0f172a;
    --darker-bg: #020617;
    --light-text: #f8fafc;
    --gray-text: #94a3b8;
    --border-color: rgba(255, 255, 255, 0.1);
    --glass-bg: rgba(255, 255, 255, 0.1);
    --glass-border: rgba(255, 255, 255, 0.2);
    --gradient-primary: linear-gradient(135deg, #3b82f6, #8b5cf6);
    --gradient-secondary: linear-gradient(135deg, #8b5cf6, #ec4899);
    --gradient-accent: linear-gradient(135deg, #ec4899, #f97316);
    --shadow-light: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
    --shadow-medium: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
    --shadow-heavy: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
}

body {
    font-family: 'Inter', sans-serif;
    line-height: 1.6;
    color: var(--light-text);
    background: linear-gradient(135deg, var(--dark-bg) 0%, #1e293b 50%, var(--dark-bg) 100%);
    overflow-x: hidden;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

/* Navigation */
.navbar {
    position: fixed;
    top: 0;
    width: 100%;
    background: rgba(15, 23, 42, 0.95);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid var(--border-color);
    z-index: 1000;
    transition: all 0.3s ease;
}

.nav-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: 70px;
}

.nav-logo {
    display: flex;
    align-items: center;
    gap: 10px;
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--light-text);
}

.nav-logo i {
    color: var(--primary-color);
    font-size: 1.8rem;
}

.nav-menu {
    display: flex;
    align-items: center;
    gap: 30px;
}

.nav-link {
    color: var(--light-text);
    text-decoration: none;
    font-weight: 500;
    transition: all 0.3s ease;
    position: relative;
}

.nav-link:hover {
    color: var(--primary-color);
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--gradient-primary);
    transition: width 0.3s ease;
}

.nav-link:hover::after {
    width: 100%;
}

.btn-login {
    background: var(--gradient-primary);
    padding: 10px 20px;
    border-radius: 25px;
    color: white;
    font-weight: 600;
}

.btn-login:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-medium);
}

/* Mobile Navigation */
.nav-toggle {
    display: none;
    flex-direction: column;
    cursor: pointer;
    gap: 4px;
}

.nav-toggle .bar {
    width: 25px;
    height: 3px;
    background: var(--light-text);
    transition: all 0.3s ease;
    border-radius: 2px;
}

.nav-toggle.active .bar:nth-child(1) {
    transform: rotate(45deg) translate(5px, 5px);
}

.nav-toggle.active .bar:nth-child(2) {
    opacity: 0;
}

.nav-toggle.active .bar:nth-child(3) {
    transform: rotate(-45deg) translate(7px, -6px);
}

/* Hero Section */
.hero {
    min-height: 100vh;
    display: flex;
    align-items: center;
    position: relative;
    padding: 120px 0 80px;
    overflow: hidden;
}

.hero-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1;
}

.particles {
    position: absolute;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 20% 80%, rgba(59, 130, 246, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 80% 20%, rgba(139, 92, 246, 0.1) 0%, transparent 50%),
                radial-gradient(circle at 40% 40%, rgba(236, 72, 153, 0.1) 0%, transparent 50%);
}

.floating-shapes {
    position: absolute;
    width: 100%;
    height: 100%;
}

.shape {
    position: absolute;
    border-radius: 50%;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    opacity: 0.1;
    animation: float 6s ease-in-out infinite;
}

.shape-1 {
    width: 100px;
    height: 100px;
    top: 20%;
    left: 10%;
    animation-delay: 0s;
}

.shape-2 {
    width: 150px;
    height: 150px;
    top: 60%;
    right: 15%;
    animation-delay: 2s;
}

.shape-3 {
    width: 80px;
    height: 80px;
    bottom: 20%;
    left: 20%;
    animation-delay: 4s;
}

@keyframes float {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    50% { transform: translateY(-20px) rotate(180deg); }
}

.hero-content {
    width: 100%;
    z-index: 1;
}

.hero-text {
    max-width: 600px;
}

.hero-badge {
    display: inline-flex;
    align-items: center;
    gap: 8px;
    background: rgba(59, 130, 246, 0.1);
    border: 1px solid rgba(59, 130, 246, 0.3);
    border-radius: 50px;
    padding: 8px 16px;
    margin-bottom: 24px;
    font-size: 0.9rem;
    font-weight: 500;
    color: var(--primary-color);
}

.hero-badge i {
    font-size: 0.8rem;
}

.hero-title {
    font-size: 3.5rem;
    font-weight: 800;
    line-height: 1.1;
    margin-bottom: 24px;
    background: linear-gradient(135deg, var(--light-text) 0%, #cbd5e1 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.gradient-text {
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.hero-description {
    font-size: 1.25rem;
    line-height: 1.6;
    margin-bottom: 32px;
    color: var(--gray-text);
}

.highlight {
    color: var(--light-text);
    font-weight: 600;
}

.hero-stats {
    display: flex;
    gap: 32px;
    margin-bottom: 32px;
}

.stat-item {
    text-align: center;
}

.stat-number {
    font-size: 2rem;
    font-weight: 700;
    color: var(--primary-color);
    display: block;
}

.stat-label {
    font-size: 0.9rem;
    color: var(--gray-text);
    font-weight: 500;
}

.hero-buttons {
    display: flex;
    gap: 16px;
    margin-bottom: 40px;
}

.btn-hero {
    padding: 16px 32px;
    font-size: 1.1rem;
    font-weight: 600;
}

.hero-trust {
    margin-top: 32px;
}

.trust-label {
    display: block;
    font-size: 0.9rem;
    color: var(--gray-text);
    margin-bottom: 16px;
}

.trust-logos {
    display: flex;
    gap: 24px;
    align-items: center;
}

.trust-logo {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid var(--border-color);
    border-radius: 8px;
    padding: 8px 16px;
    font-size: 0.9rem;
    font-weight: 500;
    color: var(--gray-text);
    transition: all 0.3s ease;
}

.trust-logo:hover {
    background: rgba(255, 255, 255, 0.1);
    border-color: var(--primary-color);
    color: var(--light-text);
}

/* Hero Showcase - Advertisement Style */
.hero-showcase {
    display: grid;
    grid-template-columns: 1fr;
    gap: 24px;
    max-width: 500px;
}

.showcase-main {
    order: 1;
}

.main-card {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid var(--glass-border);
    border-radius: 20px;
    padding: 24px;
    backdrop-filter: blur(20px);
    position: relative;
    overflow: hidden;
}

.main-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 2px;
    background: var(--gradient-primary);
}

.card-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 20px;
}

.card-avatar {
    width: 50px;
    height: 50px;
    background: var(--gradient-primary);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.5rem;
}

.card-status {
    display: flex;
    align-items: center;
    gap: 8px;
    font-size: 0.9rem;
    color: var(--primary-color);
    font-weight: 500;
}

.status-dot {
    width: 8px;
    height: 8px;
    background: var(--primary-color);
    border-radius: 50%;
    animation: pulse 2s infinite;
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
}

.card-content h3 {
    font-size: 1.3rem;
    font-weight: 600;
    margin-bottom: 8px;
    color: var(--light-text);
}

.card-content p {
    color: var(--gray-text);
    margin-bottom: 20px;
    font-size: 0.95rem;
}

.demo-stats {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 16px;
    margin-bottom: 20px;
}

.demo-stat {
    text-align: center;
    padding: 12px;
    background: rgba(255, 255, 255, 0.03);
    border-radius: 12px;
    border: 1px solid var(--border-color);
}

.stat-value {
    display: block;
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--primary-color);
    margin-bottom: 4px;
}

.stat-desc {
    font-size: 0.8rem;
    color: var(--gray-text);
}

.card-actions {
    text-align: center;
}

.btn-demo {
    background: var(--gradient-primary);
    color: white;
    border: none;
    padding: 12px 24px;
    border-radius: 25px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    width: 100%;
}

.btn-demo:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-medium);
}

.showcase-features {
    order: 2;
    display: grid;
    gap: 16px;
}

.feature-highlight {
    display: flex;
    align-items: center;
    gap: 16px;
    padding: 16px;
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid var(--border-color);
    border-radius: 12px;
    transition: all 0.3s ease;
}

.feature-highlight:hover {
    background: rgba(255, 255, 255, 0.05);
    border-color: var(--primary-color);
    transform: translateX(5px);
}

.feature-icon {
    width: 40px;
    height: 40px;
    background: var(--gradient-secondary);
    border-radius: 10px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.1rem;
}

.feature-content h4 {
    font-size: 1rem;
    font-weight: 600;
    color: var(--light-text);
    margin-bottom: 4px;
}

.feature-content p {
    font-size: 0.85rem;
    color: var(--gray-text);
}

.promo-banner {
    order: 3;
    background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
    border-radius: 16px;
    padding: 20px;
    color: white;
    position: relative;
    overflow: hidden;
}

.promo-banner::before {
    content: '';
    position: absolute;
    top: -50%;
    right: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.1) 0%, transparent 70%);
    animation: rotate 20s linear infinite;
}

@keyframes rotate {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
}

.promo-content {
    display: flex;
    align-items: center;
    gap: 16px;
    margin-bottom: 16px;
    position: relative;
    z-index: 1;
}

.promo-icon {
    font-size: 2rem;
}

.promo-text {
    display: flex;
    flex-direction: column;
}

.promo-highlight {
    font-weight: 700;
    font-size: 1.1rem;
    margin-bottom: 4px;
}

.promo-desc {
    font-size: 0.9rem;
    opacity: 0.9;
}

.promo-timer {
    text-align: center;
    position: relative;
    z-index: 1;
}

.timer-label {
    font-size: 0.9rem;
    opacity: 0.9;
    margin-bottom: 8px;
}

.timer-display {
    display: flex;
    justify-content: center;
    gap: 8px;
    font-family: 'Courier New', monospace;
    font-weight: 700;
    font-size: 1.2rem;
}

.timer-unit {
    background: rgba(255, 255, 255, 0.2);
    padding: 8px 12px;
    border-radius: 8px;
    min-width: 40px;
    text-align: center;
}

.showcase-card {
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.15), rgba(139, 92, 246, 0.15));
    backdrop-filter: blur(20px);
    border: 1px solid var(--glass-border);
    border-radius: 25px;
    padding: 40px;
    text-align: center;
    position: relative;
    overflow: hidden;
    transition: all 0.4s ease;
}

.showcase-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: var(--gradient-primary);
    border-radius: 25px 25px 0 0;
}

.showcase-card:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: var(--shadow-heavy);
    border-color: var(--primary-color);
}

.card-icon {
    width: 70px;
    height: 70px;
    background: var(--gradient-primary);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 20px;
    font-size: 1.8rem;
    color: white;
    animation: pulse-glow 2s infinite;
}

.showcase-card h3 {
    font-size: 1.8rem;
    font-weight: 700;
    margin-bottom: 15px;
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.showcase-card p {
    color: var(--gray-text);
    font-size: 1.1rem;
    line-height: 1.6;
}

.card-badge {
    position: absolute;
    top: 20px;
    right: 20px;
    background: var(--gradient-accent);
    color: white;
    padding: 8px 16px;
    border-radius: 20px;
    font-size: 0.85rem;
    font-weight: 600;
    animation: badge-bounce 2s infinite;
}

.showcase-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: 20px;
}

.mini-card {
    background: var(--glass-bg);
    backdrop-filter: blur(10px);
    border: 1px solid var(--glass-border);
    border-radius: 20px;
    padding: 25px 15px;
    text-align: center;
    transition: all 0.3s ease;
    cursor: pointer;
}

.mini-card:hover {
    transform: translateY(-5px);
    border-color: var(--primary-color);
    background: linear-gradient(135deg, rgba(59, 130, 246, 0.1), rgba(139, 92, 246, 0.1));
}

.mini-card i {
    font-size: 1.5rem;
    color: var(--primary-color);
    margin-bottom: 10px;
    display: block;
}

.mini-card span {
    font-weight: 600;
    font-size: 0.9rem;
    color: var(--light-text);
}

.promo-banner {
    background: linear-gradient(135deg, #ec4899, #f97316);
    border-radius: 20px;
    padding: 25px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    box-shadow: var(--shadow-medium);
    animation: glow-pulse 3s infinite;
}

.promo-text {
    display: flex;
    flex-direction: column;
    gap: 5px;
}

.promo-highlight {
    font-size: 1.1rem;
    font-weight: 700;
    color: white;
}

.promo-text span:last-child {
    font-size: 0.95rem;
    color: rgba(255, 255, 255, 0.9);
}

.promo-timer {
    background: rgba(255, 255, 255, 0.2);
    padding: 10px 20px;
    border-radius: 25px;
    color: white;
    font-weight: 600;
    font-size: 0.9rem;
    backdrop-filter: blur(10px);
}

/* Animations */
@keyframes pulse-glow {
    0%, 100% { box-shadow: 0 0 20px rgba(59, 130, 246, 0.3); }
    50% { box-shadow: 0 0 30px rgba(59, 130, 246, 0.6); }
}

@keyframes badge-bounce {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-3px); }
}

@keyframes glow-pulse {
    0%, 100% { box-shadow: 0 0 20px rgba(236, 72, 153, 0.3); }
    50% { box-shadow: 0 0 40px rgba(236, 72, 153, 0.6); }
}

/* Features Section */
.features {
    padding: 100px 0;
    background: linear-gradient(135deg, rgba(15, 23, 42, 0.8) 0%, rgba(30, 41, 59, 0.8) 100%);
}

.section-header {
    text-align: center;
    margin-bottom: 80px;
}

.section-badge {
    display: inline-flex;
    align-items: center;
    gap: 8px;
    background: rgba(139, 92, 246, 0.1);
    border: 1px solid rgba(139, 92, 246, 0.3);
    border-radius: 50px;
    padding: 8px 16px;
    margin-bottom: 24px;
    font-size: 0.9rem;
    font-weight: 500;
    color: var(--secondary-color);
}

.section-badge i {
    font-size: 0.8rem;
}

.section-title {
    font-size: 3rem;
    font-weight: 700;
    margin-bottom: 24px;
    line-height: 1.2;
}

.section-description {
    font-size: 1.25rem;
    color: var(--gray-text);
    max-width: 600px;
    margin: 0 auto;
    line-height: 1.6;
}

.features-showcase {
    display: grid;
    gap: 60px;
}

.feature-main {
    display: flex;
    justify-content: center;
}

.feature-card.feature-primary {
    max-width: 600px;
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid var(--glass-border);
    border-radius: 24px;
    padding: 40px;
    backdrop-filter: blur(20px);
    position: relative;
    overflow: hidden;
}

.feature-card.feature-primary::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 3px;
    background: var(--gradient-primary);
}

.feature-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 24px;
}

.feature-icon-large {
    width: 80px;
    height: 80px;
    background: var(--gradient-primary);
    border-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 2rem;
}

.feature-badge {
    background: var(--gradient-accent);
    color: white;
    padding: 6px 16px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
}

.feature-title {
    font-size: 1.8rem;
    font-weight: 700;
    margin-bottom: 16px;
    color: var(--light-text);
}

.feature-description {
    font-size: 1.1rem;
    color: var(--gray-text);
    line-height: 1.6;
    margin-bottom: 32px;
}

.feature-metrics {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 24px;
    margin-bottom: 32px;
}

.metric {
    text-align: center;
    padding: 20px;
    background: rgba(255, 255, 255, 0.03);
    border-radius: 16px;
    border: 1px solid var(--border-color);
}

.metric-value {
    display: block;
    font-size: 2rem;
    font-weight: 700;
    color: var(--primary-color);
    margin-bottom: 8px;
}

.metric-label {
    font-size: 0.9rem;
    color: var(--gray-text);
    font-weight: 500;
}

.feature-actions {
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 20px;
}

.btn-feature {
    background: var(--gradient-primary);
    color: white;
    border: none;
    padding: 14px 28px;
    border-radius: 25px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
}

.btn-feature:hover {
    transform: translateY(-2px);
    box-shadow: var(--shadow-medium);
}

.link-feature {
    color: var(--primary-color);
    text-decoration: none;
    font-weight: 500;
    transition: all 0.3s ease;
}

.link-feature:hover {
    color: var(--secondary-color);
}

.features-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 32px;
}

.feature-card.feature-secondary {
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid var(--border-color);
    border-radius: 20px;
    padding: 32px;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.feature-card.feature-secondary::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 3px;
    background: var(--gradient-secondary);
    transform: scaleX(0);
    transition: transform 0.3s ease;
}

.feature-card.feature-secondary:hover::before {
    transform: scaleX(1);
}

.feature-card.feature-secondary:hover {
    transform: translateY(-8px);
    background: rgba(255, 255, 255, 0.05);
    border-color: var(--glass-border);
}

.feature-icon {
    width: 60px;
    height: 60px;
    background: var(--gradient-secondary);
    border-radius: 16px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.5rem;
    margin-bottom: 24px;
}

.feature-title {
    font-size: 1.4rem;
    font-weight: 600;
    margin-bottom: 16px;
    color: var(--light-text);
}

.feature-description {
    font-size: 1rem;
    color: var(--gray-text);
    line-height: 1.6;
    margin-bottom: 24px;
}

.feature-highlight {
    display: flex;
    align-items: center;
    gap: 12px;
    margin-bottom: 12px;
}

.feature-highlight i {
    color: var(--primary-color);
    font-size: 0.9rem;
}

.feature-highlight span {
    font-size: 0.9rem;
    color: var(--gray-text);
}

.features-cta {
    margin-top: 80px;
    text-align: center;
    padding: 60px;
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid var(--border-color);
    border-radius: 24px;
}

.cta-content h3 {
    font-size: 2rem;
    font-weight: 700;
    margin-bottom: 16px;
    color: var(--light-text);
}

.cta-content p {
    font-size: 1.1rem;
    color: var(--gray-text);
    margin-bottom: 32px;
    max-width: 500px;
    margin-left: auto;
    margin-right: auto;
}

.cta-buttons {
    display: flex;
    gap: 20px;
    justify-content: center;
    flex-wrap: wrap;
}

/* About Section */
.about {
    padding: 100px 0;
    background: linear-gradient(135deg, rgba(15, 23, 42, 0.8) 0%, rgba(30, 41, 59, 0.8) 100%);
}

.about-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 80px;
    align-items: center;
}

.about-text {
    max-width: 600px;
}

.about-mission {
    margin: 32px 0;
    padding: 24px;
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid var(--border-color);
    border-radius: 16px;
    border-left: 4px solid var(--primary-color);
}

.about-mission h3 {
    font-size: 1.3rem;
    font-weight: 600;
    color: var(--light-text);
    margin-bottom: 12px;
}

.about-mission p {
    color: var(--gray-text);
    line-height: 1.6;
}

.about-features {
    display: grid;
    gap: 20px;
    margin: 32px 0;
}

.about-feature {
    display: flex;
    align-items: flex-start;
    gap: 16px;
    padding: 20px;
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid var(--border-color);
    border-radius: 12px;
    transition: all 0.3s ease;
}

.about-feature:hover {
    background: rgba(255, 255, 255, 0.05);
    border-color: var(--glass-border);
    transform: translateX(5px);
}

.about-feature .feature-icon {
    width: 50px;
    height: 50px;
    background: var(--gradient-primary);
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.2rem;
    flex-shrink: 0;
}

.about-feature .feature-content h4 {
    font-size: 1.1rem;
    font-weight: 600;
    color: var(--light-text);
    margin-bottom: 8px;
}

.about-feature .feature-content p {
    font-size: 0.95rem;
    color: var(--gray-text);
    line-height: 1.5;
}

.about-cta {
    display: flex;
    gap: 16px;
    margin-top: 32px;
}

.about-visual {
    position: relative;
}

.visual-container {
    position: relative;
    width: 100%;
    height: 500px;
}

.visual-main {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 2;
}

.visual-card {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid var(--glass-border);
    border-radius: 20px;
    padding: 32px;
    backdrop-filter: blur(20px);
    width: 300px;
    box-shadow: var(--shadow-heavy);
}

.card-header {
    display: flex;
    align-items: center;
    gap: 16px;
    margin-bottom: 24px;
}

.card-avatar {
    width: 50px;
    height: 50px;
    background: var(--gradient-primary);
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.3rem;
}

.card-title {
    font-size: 1.1rem;
    font-weight: 600;
    color: var(--light-text);
}

.card-content {
    margin-bottom: 24px;
}

.chart-placeholder {
    display: flex;
    align-items: end;
    gap: 8px;
    height: 120px;
    margin-bottom: 16px;
}

.chart-bar {
    width: 30px;
    background: var(--gradient-primary);
    border-radius: 4px 4px 0 0;
    transition: all 0.3s ease;
}

.chart-bar:nth-child(1) { animation: growBar 1s ease-out 0.5s both; }
.chart-bar:nth-child(2) { animation: growBar 1s ease-out 0.7s both; }
.chart-bar:nth-child(3) { animation: growBar 1s ease-out 0.9s both; }
.chart-bar:nth-child(4) { animation: growBar 1s ease-out 1.1s both; }

@keyframes growBar {
    from { height: 0; }
    to { height: var(--height); }
}

.chart-stats {
    text-align: center;
    font-size: 0.9rem;
    color: var(--primary-color);
    font-weight: 500;
}

.visual-elements {
    position: absolute;
    width: 100%;
    height: 100%;
    z-index: 1;
}

.floating-icon {
    position: absolute;
    width: 60px;
    height: 60px;
    background: var(--gradient-secondary);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.5rem;
    animation: float 6s ease-in-out infinite;
}

.floating-icon:nth-child(1) {
    top: 20%;
    left: 10%;
    animation-delay: 0s;
}

.floating-icon:nth-child(2) {
    top: 60%;
    right: 15%;
    animation-delay: 2s;
}

.floating-icon:nth-child(3) {
    bottom: 20%;
    left: 20%;
    animation-delay: 4s;
}

/* CTA Section */
.cta {
    padding: 100px 0;
    background: linear-gradient(135deg, var(--primary-color) 0%, var(--secondary-color) 100%);
    position: relative;
    overflow: hidden;
}

.cta::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="grain" width="100" height="100" patternUnits="userSpaceOnUse"><circle cx="25" cy="25" r="1" fill="rgba(255,255,255,0.1)"/><circle cx="75" cy="75" r="1" fill="rgba(255,255,255,0.1)"/><circle cx="50" cy="10" r="0.5" fill="rgba(255,255,255,0.1)"/><circle cx="10" cy="60" r="0.5" fill="rgba(255,255,255,0.1)"/><circle cx="90" cy="40" r="0.5" fill="rgba(255,255,255,0.1)"/></pattern></defs><rect width="100" height="100" fill="url(%23grain)"/></svg>');
    opacity: 0.3;
}

.cta-content {
    text-align: center;
    max-width: 800px;
    margin: 0 auto;
    position: relative;
    z-index: 2;
}

.cta-badge {
    display: inline-flex;
    align-items: center;
    gap: 8px;
    background: rgba(255, 255, 255, 0.2);
    border: 1px solid rgba(255, 255, 255, 0.3);
    border-radius: 50px;
    padding: 8px 16px;
    margin-bottom: 24px;
    font-size: 0.9rem;
    font-weight: 500;
    color: white;
}

.cta-badge i {
    font-size: 0.8rem;
}

.cta-title {
    font-size: 3rem;
    font-weight: 800;
    margin-bottom: 24px;
    color: white;
    line-height: 1.2;
}

.cta-description {
    font-size: 1.25rem;
    color: rgba(255, 255, 255, 0.9);
    margin-bottom: 40px;
    line-height: 1.6;
}

.cta-benefits {
    display: flex;
    justify-content: center;
    gap: 32px;
    margin-bottom: 40px;
    flex-wrap: wrap;
}

.benefit-item {
    display: flex;
    align-items: center;
    gap: 12px;
    color: white;
    font-weight: 500;
}

.benefit-item i {
    color: rgba(255, 255, 255, 0.8);
    font-size: 0.9rem;
}

.cta-actions {
    display: flex;
    gap: 20px;
    justify-content: center;
    margin-bottom: 40px;
    flex-wrap: wrap;
}

.btn-large {
    padding: 18px 40px;
    font-size: 1.1rem;
    font-weight: 600;
}

.cta-trust {
    margin-top: 32px;
}

.cta-trust p {
    color: rgba(255, 255, 255, 0.8);
    font-size: 0.9rem;
    font-weight: 500;
}

.cta-visual {
    margin-top: 60px;
    position: relative;
    z-index: 2;
}

.cta-cards {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 24px;
    max-width: 800px;
    margin: 0 auto;
}

.cta-card {
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(255, 255, 255, 0.2);
    border-radius: 16px;
    padding: 24px;
    text-align: center;
    backdrop-filter: blur(10px);
    transition: all 0.3s ease;
}

.cta-card:hover {
    transform: translateY(-8px);
    background: rgba(255, 255, 255, 0.15);
    border-color: rgba(255, 255, 255, 0.3);
}

.cta-card .card-icon {
    width: 60px;
    height: 60px;
    background: rgba(255, 255, 255, 0.2);
    border-radius: 16px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
    font-size: 1.5rem;
    margin: 0 auto 16px;
}

.cta-card h4 {
    font-size: 1.1rem;
    font-weight: 600;
    color: white;
    margin-bottom: 8px;
}

.cta-card p {
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.8);
    line-height: 1.4;
}

/* Footer */
.footer {
    background: var(--darker-bg);
    border-top: 1px solid var(--border-color);
    padding: 60px 0 20px;
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 40px;
    margin-bottom: 40px;
}

.footer-logo {
    display: flex;
    align-items: center;
    gap: 10px;
    font-size: 1.5rem;
    font-weight: 700;
    margin-bottom: 20px;
}

.footer-logo i {
    color: var(--primary-color);
}

.footer-description {
    color: var(--gray-text);
    line-height: 1.6;
}

.footer-title {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 20px;
}

.footer-links {
    list-style: none;
}

.footer-links li {
    margin-bottom: 10px;
}

.footer-links a {
    color: var(--gray-text);
    text-decoration: none;
    transition: color 0.3s ease;
}

.footer-links a:hover {
    color: var(--primary-color);
}

.footer-links i {
    margin-right: 10px;
    color: var(--primary-color);
}

.footer-bottom {
    text-align: center;
    padding-top: 20px;
    border-top: 1px solid var(--border-color);
    color: var(--gray-text);
}

/* Responsive Design */
@media (max-width: 768px) {
    .nav-menu {
        position: fixed;
        left: -100%;
        top: 70px;
        flex-direction: column;
        background-color: var(--dark-bg);
        width: 100%;
        text-align: center;
        transition: 0.3s;
        box-shadow: 0 10px 27px rgba(0, 0, 0, 0.05);
        padding: 20px 0;
    }

    .nav-menu.active {
        left: 0;
    }

    .nav-toggle {
        display: flex;
    }

    .hero-title {
        font-size: 2.5rem;
    }

    .hero-description {
        font-size: 1.1rem;
    }

    .hero-buttons {
        flex-direction: column;
        align-items: center;
    }

    .btn {
        width: 100%;
        max-width: 300px;
        justify-content: center;
    }

    .hero-showcase {
        gap: 20px;
        margin-top: 40px;
    }

    .showcase-card {
        padding: 30px 20px;
    }

    .showcase-grid {
        grid-template-columns: repeat(2, 1fr);
        gap: 15px;
    }

    .mini-card {
        padding: 20px 10px;
    }

    .promo-banner {
        flex-direction: column;
        gap: 15px;
        text-align: center;
        padding: 20px;
    }

    .features-grid {
        grid-template-columns: 1fr;
    }

    .about-content {
        grid-template-columns: 1fr;
        text-align: center;
    }

    .section-title {
        font-size: 2rem;
    }

    .cta-title {
        font-size: 2rem;
    }
}

@media (max-width: 480px) {
    .hero-title {
        font-size: 2rem;
    }

    .hero-showcase {
        margin-top: 30px;
    }

    .showcase-grid {
        grid-template-columns: 1fr;
    }

    .showcase-card h3 {
        font-size: 1.5rem;
    }

    .card-icon {
        width: 60px;
        height: 60px;
        font-size: 1.5rem;
    }

    .feature-card {
        padding: 30px 20px;
    }
}

/* Faculty Dashboard 3D Styles */
.faculty-main {
