    font-weight: 600;
}

.controls-grid{ display:grid; grid-template-columns: 1fr 300px; gap:20px; }
.override-form{ display:flex; gap:10px; align-items:center; }
.analytics-grid{ display:grid; grid-template-columns: repeat(4, minmax(160px,1fr)); gap:15px; margin-bottom:15px; }
.mini-analytic{ background: rgba(255,255,255,0.06); border:1px solid rgba(255,255,255,0.12); border-radius:12px; padding:12px; }
.mini-analytic .label{ color: var(--gray-text); font-size: .85rem; }
.mini-analytic .value{ color: var(--light-text); font-weight:800; font-size:1.4rem; }
.analytic-lists{ display:grid; grid-template-columns: repeat(3, 1fr); gap:20px; }
.analytic-lists .list{ list-style:none; padding:0; margin:0; display:flex; flex-direction:column; gap:8px; }
.analytic-lists li{ background: rgba(255,255,255,0.06); border:1px solid rgba(255,255,255,0.12); padding:8px 10px; border-radius:10px; }

.qr-controls {
