}

.qr-placeholder {
    text-align: center;
}

.qr-icon {
    font-size: 4rem;
    color: var(--gray-text);
    margin-bottom: 20px;
}

.qr-placeholder p {
    color: var(--gray-text);
    font-size: 1.1rem;
}

.qr-info {
    display: flex;
    justify-content: center;
    gap: 40px;
    flex-wrap: wrap;
}

.info-item {
    display: flex;
    align-items: center;
    gap: 10px;
    color: var(--gray-text);
}

.info-item i {
