/* Save Indicator Animation */
@keyframes slideIn {
    from {
        opacity: 0;
        transform: translateY(-10px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.save-indicator {
    z-index: 1000;
}

/* Testimonials Section */
.testimonials {
    padding: 100px 0;
