}

.testimonial-card.testimonial-compact .testimonial-info {
    margin-left: 16px;
}

.testimonial-card.testimonial-compact .testimonial-name {
    font-size: 1.1rem;
}

.testimonial-card.testimonial-compact .testimonial-title {
    font-size: 0.85rem;
}

.testimonial-card.testimonial-compact .testimonial-content p {
    font-size: 1rem;
}

.testimonials-stats {
    display: grid;
