        grid-template-columns: 1fr;
        gap: 24px;
    }

    .testimonial-card.testimonial-featured {
        padding: 30px 20px;
    }

    .testimonial-metrics {
        grid-template-columns: 1fr;
    }

    .testimonials-stats {
        grid-template-columns: repeat(2, 1fr);
        gap: 20px;
    }

    .stat-card {
        padding: 20px;
    }

    /* About Section Mobile */
    .about-content {
        grid-template-columns: 1fr;
        gap: 40px;
        text-align: center;
    }

    .about-features {
        gap: 16px;
    }

    .about-feature {
        text-align: left;
    }

    .about-cta {
        justify-content: center;
        flex-wrap: wrap;
    }

    .visual-container {
        height: 300px;
    }

    .visual-card {
        width: 250px;
        padding: 20px;
    }

    /* CTA Section Mobile */
    .cta {
        padding: 80px 0;
    }

    .cta-title {
        font-size: 2.5rem;
    }

    .cta-description {
        font-size: 1.1rem;
    }

    .cta-benefits {
        flex-direction: column;
        align-items: center;
        gap: 20px;
    }

    .cta-actions {
        flex-direction: column;
        align-items: center;
        gap: 16px;
    }

    .cta-cards {
        grid-template-columns: 1fr;
        gap: 20px;
    }

    /* General Mobile Adjustments */
    .container {
        padding: 0 15px;
    }

    .section-title {
        font-size: 2.5rem;
    }

    .section-description {
        font-size: 1.1rem;
    }

    .floating-shapes {
        display: none;
    }

    .floating-icon {
        display: none;
    }
